mapper.select:
    SET @rownum:=0;
    SELECT * 
    FROM (SELECT @rownum:=@rownum + 1 as rownum, a.*
          FROM (SELECT id, title, content, time, updated_time, view_count, original_id, enable, level, username, board_id
          FROM ARTICLE 
	      WHERE board_id = #{board_id}
          ORDER BY time DESC, id DESC) a) b
    WHERE rownum BETWEEN #{start} AND #{end}
    LIMIT 20;

mapper.update:
UPDATE article
SET enable=0
WHERE id=#{id}

UPDATE article
SET updated_time=now(),
title=#{title},
content=#{content}
WHERE id=#{id}

mapper.select:
SELECT count(*)
FROM article_vote
WHERE article_id=#{id}

SELECT COUNT(*)
FROM ARTICLE_VOTE
WHERE article_id=#{id} AND username=#{username}
