mapper.select:
    SET @rownum:=0;
    SELECT * 
    FROM (SELECT @rownum:=@rownum + 1 as rownum, a.*
          FROM (SELECT id, title, content, time, updated_time, view_count, original_id, enable, level, username, board_id
                FROM ARTICLE 
	            WHERE board_id = #{board_id}
                ORDER BY view_count DESC, id DESC) a) b
    WHERE rownum BETWEEN #{start} AND #{end};

SET @rownum:=0; SELECT * FROM (SELECT @rownum:=@rownum + 1 as rownum, a.* FROM (SELECT id, title, content, time, updated_time, view_count, original_id, enable, level, a.username, board_id FROM ARTICLE A LEFT JOIN MEMBER M ON a.username = m.username WHERE board_id = #{board_id}
AND ${criteria} LIKE '%${search}%'
AND (title LIKE '%${search}%' OR content LIKE '%${search}%')
ORDER BY time DESC, id DESC) a) b WHERE rownum BETWEEN #{start} AND #{end};

SET @rownum:=0; SELECT * FROM (SELECT @rownum:=@rownum + 1 as rownum, a.* FROM (SELECT DISTINCT a.* FROM ARTICLE A JOIN COMMENT C ON a.id = c.article_id LEFT JOIN MEMBER M ON c.username = m.username WHERE BOARD_ID = #{board_id}
and m.name like '%${search}%'
and c.content like '%${search}%'
ORDER BY id desc) a ) b WHERE rownum BETWEEN #{start} and #{end};

mapper.update:
UPDATE article
SET title=#{title}, content=#{content}
WHERE id=#{id}

UPDATE article
SET enable=0
WHERE id=#{id}

UPDATE article
SET updated_time=now(), title=#{title}, content=#{content}
WHERE id=#{id}

mapper.select:
SELECT count(*)
FROM article_vote
WHERE article_id=#{id}

SELECT COUNT(*)
FROM ARTICLE_VOTE
WHERE article_id=#{id} AND username=#{username}

mapper.update:
UPDATE Article
SET view_count=view_count+1
WHERE ID=#{id}
