mapper.select:

SELECT id, title, content, time, view_count, original_id, level, username, board_id, updated_time
FROM ARTICLE 
WHERE ID=#{id}

mapper.update:
UPDATE article
SET updated_time=now(),
title=#{title},
content=#{content}
WHERE id=#{id}

mapper.select:
SELECT count(username)
FROM article_vote
WHERE article_id=#{id}
