mapper.update:
update groups
set link1 = #{link1}, link2=#{link2}, link3 = #{link3}
where id=#{id}

mapper.select:
select link1, link2, link3
from groups
where project_id=#{project_id} and group_no=#{group_no}

SELECT ID, GROUP_NO, LEADER_NAME, PROJECT_ID
FROM GROUPS
WHERE PROJECT_ID=#{project_id} AND GROUP_NO=#{group_no}

SELECT group_no
FROM groups
where id=#{id}
