mapper.select:
select m.name as senderName, s.id, s.content, s.time, s.checked, s.sender_username, s.receiver_username
from member m left join message s on m.username = s.sender_username 
where s.receiver_username = #{receiver_username}

SELECT id, content, time, checked, sender_username, receiver_username
FROM message 
WHERE sender_username = #{sender_username}

select m.name as senderName, s.id, s.content, s.time, s.checked, s.sender_username, s.receiver_username
from member m left join message s on m.username = s.sender_username 
where s.id = #{id}

mapper.update:
update message
set checked =1
where id = #{id};
